import { test } from "./test";

export const initialState = {
  questionNum: 1,
  test: test,
};

const reducer = (state, action) => {
  switch (action.type) {
    case "NEXT_QUESTION":
      return {
        ...state,
        questionNum: state.questionNum + 1,
      };

    case "PREVIOUS_QUESTION":
      if (state.questionNum <= 1) {
        return state;
      }
      return {
        ...state,
        questionNum: state.questionNum - 1,
      };

    default:
      return state;
  }
};

export default reducer;
