import { test, courses, vaultAssessment, vaultAssessmentPaths } from "./test";

// DataLayer
export const initialState = {
  questionNum: 1,
  // test: test,
  test: vaultAssessment,
  answers: [],
  submit: false,
  contactId: false,
  recommended: null,
  activeCourse: null,
};

// Remove Item From List
const removeFromList = (listData, id) => {
  const newList = listData.filter((item) => item.id !== id);
  return newList;
};

// Recommended Courses Filter Logic
const filterRecommended = (answers) => {
  let recommended = courses;
  // if (answers[0] === 1) {
  //   recommended = removeFromList(recommended, 1);
  // }
  if (answers[1] === 1) {
    recommended = removeFromList(recommended, 2);
  }
  if (answers[2] === 1) {
    recommended = removeFromList(recommended, 3);
  }
  if (answers[3] !== 1) {
    recommended = removeFromList(recommended, 4);
  }
  if (answers[4] !== 1) {
    recommended = removeFromList(recommended, 5);
  }
  if (answers[4] !== 2) {
    recommended = removeFromList(recommended, 6);
  }
  if (answers[4] !== 4) {
    recommended = removeFromList(recommended, 7);
  }
  if (answers[4] !== 6) {
    recommended = removeFromList(recommended, 8);
  }
  if (answers[5] === 2) {
    recommended = removeFromList(recommended, 9);
  }
  if (answers[6] === 1) {
    recommended = removeFromList(recommended, 10);
  }
  return recommended;
};

// Process select type: multi
const answerMulti = (answers, answer) => {
  if (answers[answer - 1] === 0) {
    answers[answer - 1] = 1;
  } else {
    answers[answer - 1] = 0;
  }

  return answers;
};

// onClick Event Handler
const reducer = (state, action) => {
  switch (action.type) {
    case "ANSWER":
      state.answers[state.questionNum - 1] = action.answer;

      return {
        ...state,
        answers: state.answers,
      };

    case "ANSWER_MULTI":
      let answers = state.answers[state.questionNum - 1];
      let answer = action.answer;
      console.log("🔐 Array Old: ", answers);

      if (answers) {
        answers = answerMulti(answers, answer);
      } else {
        answers = new Array(
          state.test.answers[state.questionNum - 1].options.length
        ).fill(0);
        answers[answer - 1] = 1;
      }

      console.log("🔐 Array New: ", answers);
      state.answers[state.questionNum - 1] = answers;
      // if (state.answers[state.questionNum - 1]) {
      //   console.log("🔐 Element was found!");
      //   // Edit list if existent
      //   let answers = state.answers[state.questionNum - 1];

      //   if (!answers.includes(action.answer)) {
      //     console.log("🔐 Element item added!");
      //     // Add answer to list if non-existent
      //     answers.push(action.answer);
      //   } else {
      //     console.log("🔐 Element item removed");
      //     // Remove answer from list if existent
      //     answers.splice(answers.indexOf(action.answer), 1);
      //   }
      //   answers.sort();
      //   state.answers[state.questionNum - 1] = answers;
      // } else {
      //   // Create list if non-existent
      //   state.answers[state.questionNum - 1] = [];
      //   state.answers[state.questionNum - 1].push(action.answer);
      // }

      return {
        ...state,
        answers: state.answers,
      };

    case "NEXT_QUESTION":
      if (state.answers[state.questionNum - 1] === undefined) {
        alert("Please select an answer before advancing...");
        return state;
      }
      if (state.questionNum === state.test.questions.length) {
        return {
          ...state,
          submit: true,
          questionNum: state.questionNum + 1,
        };
      }
      if (state.questionNum > state.test.questions.length) {
        return state;
      }
      return {
        ...state,
        questionNum: state.questionNum + 1,
      };

    case "PREVIOUS_QUESTION":
      if (state.questionNum === 1) {
        return state;
      }
      if (state.questionNum > state.test.questions.length) {
        return {
          ...state,
          submit: false,
          questionNum: state.questionNum - 1,
        };
      }
      return {
        ...state,
        questionNum: state.questionNum - 1,
      };

    case "SUBMIT":
      let recommended = filterRecommended(state.answers);
      return {
        ...state,
        recommended: recommended,
      };

    case "COURSE_SELECTED":
      return {
        ...state,
        activeCourse: action.course,
      };

    case "SET_CONTACT_ID":
      return {
        ...state,
        contactId: true,
      };

    default:
      return state;
  }
};

export default reducer;
