import React, { useState, useEffect } from "react";
import { useStateValue } from "./StateProvider";
import "../css/Answer.css";

function Answer() {
  const [{ questionNum, test }, dispatch] = useStateValue();
  return (
    <div className="answer">
      <div
        onClick={() => dispatch({ type: "ANSWER", answer: 1 })}
        className="answer-1"
      >
        {test.answers[questionNum - 1][0]}
      </div>
      <div
        onClick={() => dispatch({ type: "ANSWER", answer: 2 })}
        className="answer-2"
      >
        {test.answers[questionNum - 1][1]}
      </div>
    </div>
  );
}

export default Answer;
