import React from "react";
import Slider from "./Slider";
import { useStateValue } from "./StateProvider";
import { useHistory } from "react-router-dom";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import "../css/Result.css";

function Result() {
  const [{ recommended, activeCourse }] = useStateValue();
  const history = useHistory();

  return (
    <div className="result">
      {!recommended ? history.replace("/test") : null}
      <div className="result-top">
        <Slider />
      </div>
      <div className="result-bodyAnchor"></div>
      {activeCourse ? (
        <div className="result-body">
          <div className="result-viewMore">
            <ExpandMoreIcon />
          </div>
          <div className="result-courseHeader">{activeCourse?.title}</div>
          <div className="result-bodyContent">
            <div className="result-left">
              <div class="result-iframeContainer">
                <iframe
                  title={activeCourse.title}
                  src={activeCourse?.video}
                  gesture="media"
                  allow="encrypted-media"
                  allowFullScreen
                ></iframe>
              </div>
            </div>
            <div className="result-right">
              <div className="result-rightDescription">
                <h2>{activeCourse?.title}</h2>
                <p>{activeCourse?.description}</p>
              </div>
            </div>
          </div>
          <div className="result-nextStep"></div>
        </div>
      ) : null}
      <div className="result-footer"></div>
    </div>
  );
}

export default Result;
