import { test, courses, vaultAssessment, vaultAssessmentPaths } from "./test";

// DataLayer
export const initialState = {
  questionNum: 1,
  // test: test,
  test: vaultAssessment,
  answers: [],
  submit: false,
  contactId: false,
  recommended: null,
  activeCourse: null,
};

// Remove Item From List
const removeFromList = (listData, id) => {
  const newList = listData.filter((item) => item.id !== id);
  return newList;
};

// Recommended Courses Filter Logic
const filterRecommended = (answers) => {
  let recommended = courses;
  // if (answers[0] === 1) {
  //   recommended = removeFromList(recommended, 1);
  // }
  if (answers[1] === 1) {
    recommended = removeFromList(recommended, 2);
  }
  if (answers[2] === 1) {
    recommended = removeFromList(recommended, 3);
  }
  if (answers[3] !== 1) {
    recommended = removeFromList(recommended, 4);
  }
  if (answers[4] !== 1) {
    recommended = removeFromList(recommended, 5);
  }
  if (answers[4] !== 2) {
    recommended = removeFromList(recommended, 6);
  }
  if (answers[4] !== 4) {
    recommended = removeFromList(recommended, 7);
  }
  if (answers[4] !== 6) {
    recommended = removeFromList(recommended, 8);
  }
  if (answers[5] === 2) {
    recommended = removeFromList(recommended, 9);
  }
  if (answers[6] === 1) {
    recommended = removeFromList(recommended, 10);
  }
  return recommended;
};

// Process select type: multi
const answerMulti = (answers, answer) => {
  console.log("ðŸ” Clicked 2: ", answer);
  if (answers[answer - 1] === 0) {
    answers[answer - 1] = 1;
  } else if (answers[answer - 1] === 1) {
    answers[answer - 1] = 0;
  }

  return answers;
};

// onClick Event Handler
const reducer = (state, action) => {
  switch (action.type) {
    case "ANSWER":
      state.answers[state.questionNum - 1] = action.answer;

      return {
        ...state,
        answers: state.answers,
      };

    case "ANSWER_MULTI":
      const index = state.questionNum - 1;
      const answer = action.answer;

      let stateAnswers = state.answers;
      let answers = stateAnswers[index];

      if (!answers) {
        console.log("ðŸ” Array created!");
        answers = new Array(state.test.answers[index].options.length).fill(0);
      }

      if (answers[answer - 1] === 1) {
        answers[answer - 1] += 1;
        console.log("Status: Chosen!");
      } else if (answers[answer - 1] === 0) {
        answers[answer - 1] += 1;
        console.log("Status: Not Chosen!");
      }

      // answers = answerMulti(answers, answer);
      stateAnswers[index] = answers;

      return {
        ...state,
        answers: stateAnswers,
      };

    case "NEXT_QUESTION":
      if (state.answers[state.questionNum - 1] === undefined) {
        alert("Please select an answer before advancing...");
        return state;
      }
      if (state.questionNum === state.test.questions.length) {
        return {
          ...state,
          submit: true,
          questionNum: state.questionNum + 1,
        };
      }
      if (state.questionNum > state.test.questions.length) {
        return state;
      }
      return {
        ...state,
        questionNum: state.questionNum + 1,
      };

    case "PREVIOUS_QUESTION":
      if (state.questionNum === 1) {
        return state;
      }
      if (state.questionNum > state.test.questions.length) {
        return {
          ...state,
          submit: false,
          questionNum: state.questionNum - 1,
        };
      }
      return {
        ...state,
        questionNum: state.questionNum - 1,
      };

    case "SUBMIT":
      let recommended = filterRecommended(state.answers);
      return {
        ...state,
        recommended: recommended,
      };

    case "COURSE_SELECTED":
      return {
        ...state,
        activeCourse: action.course,
      };

    case "SET_CONTACT_ID":
      return {
        ...state,
        contactId: true,
      };

    default:
      return state;
  }
};

export default reducer;
