import React from "react";
import { useStateValue } from "./StateProvider";
import SliderCard from "./SliderCard";
import { scroller } from "react-scroll";
import "../css/Slider.css";

function Slider() {
  const [{ recommended }, dispatch] = useStateValue();

  return (
    <div className="slider">
      <div className="slider-header">Your Recommended Courses</div>
      <div className="slider-headerBorder"></div>
      <div className="slider-body">
        {recommended?.map((course) => (
          <SliderCard
            onClick={() => {
              scroller.scrollTo("result-bodyAnchor", {
                duration: 1500,
                delay: 0,
                smooth: "easeIn",
              });
              dispatch({ type: "COURSE_SELECTED", course: course });
            }}
            key={course.id}
            id={course.id}
            title={course.title}
            description={course.description}
          />
        ))}
      </div>
      <div className="slider-footerBorder"></div>
    </div>
  );
}

export default Slider;
