import React from "react";
import Slider from "./Slider";
import { useStateValue } from "./StateProvider";
import { useHistory } from "react-router-dom";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import CourseCard from "./CourseCard";
import { scroller } from "react-scroll";
import "../css/Result.css";

function Result() {
  const [{ recommended, activeCourse, answers }] = useStateValue();
  const history = useHistory();

  return (
    <div className="result">
      {/* Redirect If Answers Not Provided */}
      {!recommended ? history.replace("/") : null}
      {/* Warning Message */}
      {answers[0] !== 1 ? (
        <div className="result-warning">
          <h1>IMPORTANT NOTICE</h1>
          <div className="result-warningSubtext">
            <p>
              Before accessing the courses below in the Vault, we recommend that
              you finish the SFM/Mentors Getting Started modules
            </p>
            <p>
              Of course you're free to watch anything below, however it's
              important to stay focused on getting the foundations in place at
              the start of your journey. The Vault is optimised to pick up where
              the modules finish and help you on the next stages of your online
              business journey.
            </p>
          </div>
        </div>
      ) : null}
      {/* Slider */}
      <div className="result-top">
        <Slider cardType={"SliderCard"} />
      </div>
      <div className="result-bodyAnchor"></div>
      {/* Card Content */}
      {activeCourse ? (
        <div className="result-body">
          <div className="result-viewMore">
            <ExpandMoreIcon
              onClick={() => {
                scroller.scrollTo("result-courseHeader", {
                  duration: 1500,
                  delay: 0,
                  smooth: "easeIn",
                });
              }}
            />
          </div>
          <div className="result-courseHeader">{activeCourse?.title}</div>
          <div className="result-bodyContent">
            <div className="result-left">
              {/* Video Block */}
              {/* <div class="result-iframeContainer">
                <iframe
                  title={activeCourse?.title}
                  src={activeCourse?.video}
                  gesture="media"
                  allow="encrypted-media"
                  allowFullScreen
                ></iframe>
              </div> */}
              <div className="result-leftCourses">
                {/* Course Cards */}
                {activeCourse?.courses?.map((course, id) => (
                  <CourseCard
                    key={id}
                    title={course.title}
                    description={course.description}
                    img={course.img}
                    link={course.link}
                  />
                ))}
              </div>
            </div>
            <div className="result-right">
              <div className="result-rightDescription">
                <h2>{activeCourse?.title}</h2>
                <p>{activeCourse?.description}</p>
              </div>
            </div>
          </div>
          <div className="result-nextStep">
            {/* <div
              onClick={() => history.push("/")}
              className="result-nextStepButton"
            >
              View More
            </div> */}
          </div>
        </div>
      ) : null}
      <div className="result-footer"></div>
    </div>
  );
}

export default Result;
