import { test } from "./test";

export const initialState = {
  questionNum: 1,
  test: test,
  answers: [],
};

const reducer = (state, action) => {
  switch (action.type) {
    case "NEXT_QUESTION":
      return {
        ...state,
        questionNum: questionNum + 1,
      };

    case "PREVIOUS_QUESTION":
      if (questionNum <= 1) {
        return;
      }
      return {
        ...state,
        questionNum: questionNum - 1,
      };
  }
};

export default reducer;
