import React from "react";
import { Card, CardContent, Typography } from "@material-ui/core";
import { useStateValue } from "./StateProvider";
import "../css/SliderCard.css";

function SliderCard({ id, title, description, ...props }) {
  const [{ activeCourse }] = useStateValue();

  return (
    <Card
      onClick={props.onClick}
      className={`sliderCard ${activeCourse === id && "sliderCard--active"}`}
    >
      <CardContent>
        <Typography className="slidierCard-title" color="textSecondary">
          {title}
        </Typography>
        <h2>Something</h2>
        <Typography>Something More</Typography>
      </CardContent>
      <div className="sliderCard-header">{title}</div>
      <div className="sliderCard-body">{description}</div>
      <div className="sliderCard-footer"></div>
    </Card>
  );
}

export default SliderCard;
