import React from "react";
import { useStateValue } from "./StateProvider";
import { useHistory } from "react-router-dom";
import axios from "axios";
import "../css/Answer.css";

function Answer() {
  const [{ questionNum, test, answers, submit }, dispatch] = useStateValue();
  const history = useHistory();

  const handleSubmit = () => {
    dispatch({ type: "SUBMIT" });

    const personality = {
      personality: "hello",
    };

    // axios
    //   .post("http://localhost/school/testing/personalityTest.php", {
    //     personality: "hello",
    //   })
    //   .then((res) => {
    //     console.log(res);
    //   });

    history.push("/result");
  };

  return (
    <div className="answer">
      {!submit ? (
        <div className="answer-container">
          <div
            onClick={() => dispatch({ type: "ANSWER", answer: 1 })}
            className={`answer-1 ${
              answers[questionNum - 1] === 1 && "answer--selected"
            } ${answers[questionNum - 1] === 2 && "answer--notSelected"}`}
          >
            {test.answers[questionNum - 1][0]}
          </div>
          <div
            onClick={() => dispatch({ type: "ANSWER", answer: 2 })}
            className={`answer-2 ${
              answers[questionNum - 1] === 2 && "answer--selected"
            } ${answers[questionNum - 1] === 1 && "answer--notSelected"}`}
          >
            {test.answers[questionNum - 1][1]}
          </div>
        </div>
      ) : null}
      {submit ? (
        <div className="answer-submit">
          <div onClick={() => handleSubmit()} className="answer-submitButton">
            Submit Answers
          </div>
        </div>
      ) : null}
    </div>
  );
}

export default Answer;
