import {
  sfmAssessment,
  vaultAssessment,
  sfmAssessmentPaths,
  vaultAssessmentPaths,
  sfmAssessmentMessage,
  vaultAssessmentMessage,
} from "./test";

// DataLayer
export const initialState = {
  questionNum: 1,
  test: null,
  answers: [],
  submit: false,
  contactId: false,
  recommended: null,
  message: null,
  activePath: null,
  chosenPath: 0,
};

// Remove Item From List
const removeFromList = (listData, id) => {
  const newList = listData.filter((item) => item.id !== id);
  return newList;
};

// SFM Assessment Filter Answers
const sfmAssessmentFilter = (answers) => {
  let recommended = sfmAssessmentPaths;
  let message = sfmAssessmentMessage;

  if (answers[1] === 1) {
    recommended = removeFromList(recommended, 2);
  }
  if (answers[2] === 1) {
    recommended = removeFromList(recommended, 3);
  }
  if (answers[3] !== 1) {
    recommended = removeFromList(recommended, 4);
  }
  if (answers[4] !== 1) {
    recommended = removeFromList(recommended, 5);
  }
  if (answers[4] !== 2) {
    recommended = removeFromList(recommended, 6);
  }
  if (answers[4] !== 4) {
    recommended = removeFromList(recommended, 7);
  }
  if (answers[4] !== 6) {
    recommended = removeFromList(recommended, 8);
  }
  if (answers[5] === 2) {
    recommended = removeFromList(recommended, 9);
  }
  if (answers[6] === 1) {
    recommended = removeFromList(recommended, 10);
  }
  return [recommended, message];
};

// Vault Assessment Filter Answers
const vaultAssessmentFilter = (answers) => {
  let recommended = vaultAssessmentPaths;
  let message = vaultAssessmentMessage;

  // Score Increased
  // A [0], E [1], C [2], P [3], Fo [4], Fr [5], YT [6], B [7]
  // Question 1, 2, 3, 5, 6
  if (answers[0] === 1) {
    recommended[5].score += 3;
  }
  if (answers[1] === 1) {
    recommended[6].score += 3;
    recommended[7].score += 3;
  }
  if (answers[2] === 1) {
    recommended[4].score += 3;
  }
  if (answers[4] === 1) {
    recommended[0].score += 3;
  }
  if (answers[5] === 1) {
    recommended[1].score += 3;
  }
  // Question 9
  if (answers[8][0] === 1) {
    recommended[0].score += 5;
  }
  if (answers[8][1] === 1) {
    recommended[3].score += 5;
  }
  if (answers[8][2] === 1) {
    recommended[2].score += 5;
  }
  if (answers[8][3] === 1) {
    recommended[6].score += 5;
    recommended[7].score += 5;
  }
  if (answers[8][4] === 1) {
    recommended[4].score += 5;
    recommended[5].score += 5;
  }
  // Question 11
  if (answers[10][0] === 1) {
    // recommended[0].score += 5;
  }
  if (answers[10][1] === 1) {
    // recommended[0].score += 5;
  }
  if (answers[10][2] === 1) {
    recommended[1].score += 5;
  }
  if (answers[10][3] === 1) {
    recommended[6].score += 5;
    recommended[7].score += 5;
  }
  if (answers[10][4] === 1) {
    recommended[0].score += 5;
  }
  if (answers[10][5] === 1) {
    recommended[3].score += 5;
  }
  if (answers[10][6] === 1) {
    recommended[4].score += 5;
  }
  // Score Decreased

  // Eliminating Answers
  // Question 10
  if (answers[9][0] === 1) {
    recommended = removeFromList(recommended, 5);
  }
  if (answers[9][1] === 1) {
    recommended = removeFromList(recommended, 7);
  }
  if (answers[9][2] === 1) {
    recommended = removeFromList(recommended, 8);
  }
  if (answers[9][3] === 1) {
    recommended = removeFromList(recommended, 6);
  }
  if (answers[9][4] === 1) {
    recommended = removeFromList(recommended, 2);
  }
  if (answers[9][5] === 1) {
    recommended = removeFromList(recommended, 3);
  }
  if (answers[9][6] === 1) {
    // recommended = removeFromList(recommended, 5);
    // Warning If Question Answered
  }

  // Sort Recommended by Score
  recommended.sort(function (a, b) {
    return b.score - a.score;
  });

  return [recommended, message];
};

// Recommended Paths Filter VAULT Logic

// onClick Event Handler
const reducer = (state, action) => {
  switch (action.type) {
    case "ANSWER":
      state.answers[state.questionNum - 1] = action.answer;

      return {
        ...state,
        answers: state.answers,
      };

    case "NEXT_QUESTION":
      if (state.questionNum === state.test.questions.length) {
        return {
          ...state,
          submit: true,
          questionNum: state.questionNum + 1,
        };
      }
      if (state.questionNum > state.test.questions.length) {
        return state;
      }
      return {
        ...state,
        questionNum: state.questionNum + 1,
      };

    case "PREVIOUS_QUESTION":
      if (state.questionNum === 1) {
        return state;
      }
      if (state.questionNum > state.test.questions.length) {
        return {
          ...state,
          submit: false,
          questionNum: state.questionNum - 1,
        };
      }
      return {
        ...state,
        questionNum: state.questionNum - 1,
      };

    case "SUBMIT":
      let sessionTest = sessionStorage.getItem("sessionTest");
      let recommended = "";
      let message = "";
      if (sessionTest === "sfm-assessment") {
        [recommended, message] = sfmAssessmentFilter(state.answers);
      } else if (sessionTest === "vault-assessment") {
        [recommended, message] = vaultAssessmentFilter(state.answers);
      }
      return {
        ...state,
        recommended: recommended,
        message: message,
      };

    case "PATH_SELECTED":
      return {
        ...state,
        activePath: action.path,
      };

    case "SET_CONTACT_ID":
      return {
        ...state,
        contactId: true,
      };

    case "SET_TEST":
      let tempTest;
      if (action.test === "sfm-assessment") {
        tempTest = sfmAssessment;
      } else if (action.test === "vault-assessment") {
        tempTest = vaultAssessment;
      }

      return {
        ...state,
        test: tempTest,
      };

    case "CHOOSE_PATH":
      return {
        ...state,
        chosenPath: state.answers,
      };

    default:
      return state;
  }
};

export default reducer;
