import React from "react";
import { useStateValue } from "./StateProvider";
import { useHistory } from "react-router-dom";
import axios from "axios";
import "../css/Answer.css";

function Answer() {
  const [
    { questionNum, test, answers, submit, contactId },
    dispatch,
  ] = useStateValue();
  const history = useHistory();

  const handleMultiAnswer = (array, i) => {
    if (!array) {
      array = [0, 0, 0, 0, 0, 0];
    }
    if (array[i] === 0) {
      array[i] = 1;
    } else {
      array[i] = 0;
    }

    return array;
  };

  const handleOnClick = (e) => {
    if (e.target.style.color === "yellowgreen") {
      e.target.style.borderColor = "";
      e.target.style.color = "";
    } else {
      e.target.style.borderColor = "yellowgreen";
      e.target.style.color = "yellowgreen";
    }
  };

  const handleSubmit = () => {
    dispatch({ type: "SUBMIT" });

    // Add ...php?assessment=sfm-member
    if (contactId) {
      if (sessionStorage.getItem("sessionContactId")) {
        axios
          .post(
            "http://localhost/scrapthe9to5.com/scripts/IS/ISVaultAssessment.php",
            {
              contactId: sessionStorage.getItem("sessionContactId"),
              answers: answers,
            }
          )
          .then((res) => {
            console.log(res);
          });
      }
    }
    history.push("/result");
  };

  return (
    <div className="answer">
      {!submit ? (
        <div className="answer-container">
          {test.answers[questionNum - 1].options.map((option, i) => (
            <div
              key={i}
              onload={(e) => {
                e.target.style.borderColor = "";
                e.target.style.color = "";
              }}
              onClick={(e) => {
                if (test.answers[questionNum - 1].select === "single") {
                  dispatch({ type: "ANSWER", answer: i + 1 });
                  dispatch({ type: "NEXT_QUESTION" });
                } else if (test.answers[questionNum - 1].select === "multi") {
                  answers[questionNum - 1] = handleMultiAnswer(
                    answers[questionNum - 1],
                    i
                  );
                  console.log(e.target.style.color);
                  if (e.target.style.color === "yellowgreen") {
                    e.target.style.borderColor = "";
                    e.target.style.color = "";
                  } else {
                    e.target.style.borderColor = "yellowgreen";
                    e.target.style.color = "yellowgreen";
                  }
                }
              }}
              className={`answer-option
              ${
                answers[questionNum - 1]?.length > 1
                  ? answers[questionNum - 1][i] === 1
                    ? "answer--selected"
                    : ""
                  : answers[questionNum - 1] === i + 1
                  ? "answer--selected"
                  : ""
              }`}
            >
              {option}
            </div>
          ))}
        </div>
      ) : null}
      {submit ? (
        <div className="answer-submit">
          <div onClick={() => handleSubmit()} className="answer-submitButton">
            Submit Answers
          </div>
        </div>
      ) : null}
    </div>
  );
}

export default Answer;
