import { test, courses } from "./test";

export const initialState = {
  questionNum: 1,
  test: test,
  answers: [],
  submit: false,
  recommended: { test: "Hei" },
};

const filterRecommended = (answers) => {
  let recommended = courses;
  if (answers[0] === 1) {
    delete recommended["C3"];
  } else {
    delete recommended["C2"];
  }
  if (answers[4] === 2) {
    delete recommended["C1"];
  }
  return recommended;
};

const reducer = (state, action) => {
  switch (action.type) {
    case "ANSWER":
      state.answers[state.questionNum - 1] = action.answer;

      return {
        ...state,
        answers: state.answers,
      };

    case "NEXT_QUESTION":
      if (state.questionNum === state.test.questions.length) {
        return {
          ...state,
          submit: true,
          questionNum: state.questionNum + 1,
        };
      }
      if (state.questionNum > state.test.questions.length) {
        return state;
      }
      if (state.answers[state.questionNum - 1] === undefined) {
        alert("Please select an answer before advancing...");
        return state;
      }
      return {
        ...state,
        questionNum: state.questionNum + 1,
      };

    case "PREVIOUS_QUESTION":
      if (state.questionNum === 1) {
        return state;
      }
      if (state.questionNum > state.test.questions.length) {
        return {
          ...state,
          submit: false,
          questionNum: state.questionNum - 1,
        };
      }
      return {
        ...state,
        questionNum: state.questionNum - 1,
      };

    case "SUBMIT":
      let recommended = filterRecommended(state.answers);
      return {
        ...state,
        recommended: recommended,
      };

    default:
      return state;
  }
};

export default reducer;
