import React from "react";
import { useStateValue } from "./StateProvider";
import { useHistory } from "react-router-dom";
import axios from "axios";
import "../css/Answer.css";

function Answer() {
  const [{ questionNum, test, answers, submit }, dispatch] = useStateValue();
  const history = useHistory();

  const handleSubmit = () => {
    dispatch({ type: "SUBMIT" });

    const personality = {
      personality: "hello",
    };

    // axios
    //   .post("http://localhost/school/testing/personalityTest.php", {
    //     personality: "hello",
    //   })
    //   .then((res) => {
    //     console.log(res);
    //   });

    history.push("/result");
  };

  return (
    <div className="answer">
      {!submit ? (
        <div className="answer-container">
          {test.answers[questionNum - 1].map((option, i) => (
            <div
              key={i}
              onClick={() => {
                dispatch({ type: "ANSWER", answer: i + 1 });
                dispatch({ type: "NEXT_QUESTION" });
              }}
              className={`answer-option
              ${answers[questionNum - 1] === i + 1 && "answer--selected"}`}
            >
              {option}
            </div>
          ))}
        </div>
      ) : null}
      {submit ? (
        <div className="answer-submit">
          <div onClick={() => handleSubmit()} className="answer-submitButton">
            Submit Answers
          </div>
        </div>
      ) : null}
    </div>
  );
}

export default Answer;
