import { test } from "./test";

export const initialState = {
  questionNum: 1,
};

const reducer = (state, action) => {
  switch (action.type) {
    case "NEXT_QUESTION":
      console.log(action.type);
      return {
        ...state,
        questionNum: state.questionNum + 1,
      };

    case "PREVIOUS_QUESTION":
      if (state.questionNum <= 1) {
        return;
      }
      return {
        ...state,
        questionNum: state.questionNum - 1,
      };
  }
};

export default reducer;
