import React from "react";
import { useStateValue } from "./StateProvider";
import SliderCard from "./SliderCard";
import "../css/Slider.css";

function Slider() {
  const [{ answers }] = useStateValue();

  return (
    <div className="slider">
      <div className="slider-header">Your Recommended Courses</div>
      <div className="slider-body">
        <SliderCard
          title={"Affiliate Marketing"}
          description={
            "This is a course description mate... What you gon do 'bout it?!"
          }
        />
        <SliderCard
          title={"Forex Trading"}
          description={
            "This is a course description mate... What you gon do 'bout it?!"
          }
        />
        <SliderCard
          title={"Social Media Agency"}
          description={
            "This is a course description mate... What you gon do 'bout it?!"
          }
        />
      </div>
      <div className="slider-footer"></div>
    </div>
  );
}

export default Slider;
