import React from "react";
import { useStateValue } from "./StateProvider";
import "../css/Answer.css";

function Answer() {
  const [{ questionNum, test, answers, submit }, dispatch] = useStateValue();
  return (
    <div className="answer">
      {!submit ? (
        <div
          onClick={() => dispatch({ type: "ANSWER", answer: 1 })}
          className={`answer-1 ${
            answers[questionNum - 1] === 1 && "answer--selected"
          } ${answers[questionNum - 1] === 2 && "answer--notSelected"}`}
        >
          {test.answers[questionNum - 1][0]}
        </div>
      ) : null}
      {!submit ? (
        <div
          onClick={() => dispatch({ type: "ANSWER", answer: 2 })}
          className={`answer-2 ${
            answers[questionNum - 1] === 2 && "answer--selected"
          } ${answers[questionNum - 1] === 1 && "answer--notSelected"}`}
        >
          {test.answers[questionNum - 1][1]}
        </div>
      ) : null}
    </div>
  );
}

export default Answer;
