import { test } from "./test";

export const initialState = {
  questionNum: 1,
  test: test,
  answers: [],
};

const reducer = (state, action) => {
  switch (action.type) {
    case "ANSWER":
      let newAnswer = [...state.answers];
      newAnswer[state.qustionNum - 1] = action.answer;

      return {
        ...state,
        answers: newAnswer,
      };

    case "NEXT_QUESTION":
      if (state.questionNum == state.test.questions.length) {
        return state;
      }
      if (!state.answers[state.questionNum - 1]) {
        console.warn("Please select an answer before advancing!");
      }
      return {
        ...state,
        questionNum: state.questionNum + 1,
      };

    case "PREVIOUS_QUESTION":
      if (state.questionNum <= 1) {
        return state;
      }
      return {
        ...state,
        questionNum: state.questionNum - 1,
      };

    default:
      return state;
  }
};

export default reducer;
