import React from "react";
import { useStateValue } from "./StateProvider";
import SliderCard from "./SliderCard";
import CourseCard from "./CourseCard";
import { scroller } from "react-scroll";
import "../css/Slider.css";

function Slider({ header, cardType, border }) {
  const [{ recommended, activeCourse }, dispatch] = useStateValue();

  return (
    <div className="slider">
      <div className="slider-header">{header}</div>
      {border && <div className="slider-headerBorder"></div>}
      <div className="slider-body">
        {cardType === "SliderCard" &&
          recommended?.map((course) => (
            <SliderCard
              onClick={() => {
                scroller.scrollTo("result-bodyAnchor", {
                  duration: 1200,
                  delay: 0,
                  smooth: "easeIn",
                });
                dispatch({ type: "COURSE_SELECTED", course: course });
              }}
              key={course.id}
              id={course.id}
              title={course.title}
              description={course.description}
            />
          ))}
        {cardType === "CourseCard" &&
          activeCourse?.courses?.map((course) => (
            <CourseCard
              title={course.title}
              description={course.description}
              img={course.img}
              link={course.link}
            />
          ))}
      </div>
      {border && <div className="slider-footerBorder"></div>}
    </div>
  );
}

export default Slider;
