import { test, courses } from "./test";

// DataLayer
export const initialState = {
  questionNum: 1,
  test: test,
  answers: [],
  submit: false,
  recommended: [],
  activeCourse: null,
};

const filterList = (listData) => {
  const newList = listData.filter((item) => item !== 2);
  console.log(newList);
};

const prevList = [1, 2, 3, 4, 5];
filterList(prevList);

// Remove Item From List
// const removeFromList = (listData, id) => {
//   const newList = listData.list.filter((item) => item.id !== id);
//   return newList;
