import { test, courses } from "./test";

// DataLayer
export const initialState = {
  questionNum: 1,
  test: test,
  answers: [],
  submit: false,
  recommended: [],
  activeCourse: null,
};

const list = [1, 2, 3, 4, 5];
const filterList = (list) => {
  const newList = list.list.filter((item) => item !== 2);
  console.log(newList);
};
filterList(list);

// Remove Item From List
const removeFromList = (listData, id) => {
  const newList = listData.list.filter((item) => item.id !== id);
  return newList;
};

// Recommended Courses Filter Logic
const filterRecommended = (answers) => {
  let recommended = courses;
  if (answers[0] === 1) {
    recommended = removeFromList(recommended, "C3");
  } else {
    recommended = removeFromList(recommended, "C2");
  }
  if (answers[4] === 2) {
    recommended = removeFromList(recommended, "C1");
  } else {
    recommended = removeFromList(recommended, "C4");
  }
  return recommended;
};

// onClick Event Handler
const reducer = (state, action) => {
  switch (action.type) {
    case "ANSWER":
      state.answers[state.questionNum - 1] = action.answer;

      return {
        ...state,
        answers: state.answers,
      };

    case "NEXT_QUESTION":
      if (state.questionNum === state.test.questions.length) {
        return {
          ...state,
          submit: true,
          questionNum: state.questionNum + 1,
        };
      }
      if (state.questionNum > state.test.questions.length) {
        return state;
      }
      if (state.answers[state.questionNum - 1] === undefined) {
        alert("Please select an answer before advancing...");
        return state;
      }
      return {
        ...state,
        questionNum: state.questionNum + 1,
      };

    case "PREVIOUS_QUESTION":
      if (state.questionNum === 1) {
        return state;
      }
      if (state.questionNum > state.test.questions.length) {
        return {
          ...state,
          submit: false,
          questionNum: state.questionNum - 1,
        };
      }
      return {
        ...state,
        questionNum: state.questionNum - 1,
      };

    case "SUBMIT":
      let recommended = filterRecommended(state.answers);
      return {
        ...state,
        recommended: recommended,
      };

    case "COURSE_SELECTED":
      return {
        ...state,
        activeCourse: action.course,
      };

    default:
      return state;
  }
};

export default reducer;
